import { clientsClaim } from 'workbox-core';
import { cleanupOutdatedCaches, precacheAndRoute } from 'workbox-precaching';

// Clean up old cache versions
cleanupOutdatedCaches();

// Take control immediately (optional, good for PWA updates)
self.skipWaiting();
clientsClaim();

// Precache the assets generated by the build script
// This variable 'self.__WB_MANIFEST' will be replaced by the script
precacheAndRoute(self.__WB_MANIFEST);

